package Chapter02;

public class EX03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수 -> 자료형과 일치하는 자료를 대입
		int data = 10;
		System.out.println(data);
		
		data = 20;
		System.out.println(data);
		
		data = 3.33; // Type mismatch: cannot convert from float to int
		System.out.println(data);
	}

}