package Chapter03;

public class EX01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 산술 연산자
				int data1, data2;
				data1 = data2 = 5;
				System.out.println(data1 + "+" + data2 + "=" + (data1 + data2));
				System.out.println(data1 + "-" + data2 + "=" + (data1 - data2));
				System.out.println(data1 + "*" + data2 + "=" + (data1 * data2));
				System.out.println(data1 + "/" + data2 + "=" + (data1 / data2));
				System.out.println(data1 + "%" + data2 + "=" + (data1 % data2));
	}

}
