package Chapter05;

public class EX02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 문자열을 반복문 횟수 역순으로 하여 10번 출력
		for (int i = 10; 1 <= i; i--) {
			System.out.println("String");
		}
		
		// 순서 확인
		for (int i = 10; 1 <= i; i--) {
			System.out.println("i: " + i);
		}
	}

}